/**
 * Blessed
 */

function blessed() {
  return blessed.program.apply(null, arguments);
}

blessed.program = blessed.Program = require('blessed/lib/program');
blessed.tput = blessed.Tput = require('blessed/lib/tput');
blessed.widget = require('blessed/lib/widget');
blessed.colors = require('blessed/lib/colors');
blessed.unicode = require('blessed/lib/unicode');
blessed.helpers = require('blessed/lib/helpers');

blessed.helpers.sprintf = blessed.tput.sprintf;
blessed.helpers.tryRead = blessed.tput.tryRead;
blessed.helpers.merge(blessed, blessed.helpers);

blessed.helpers.merge(blessed, blessed.widget);

/**
 * Expose
 */

module.exports = blessed;
